mapper.update:
update posts
set viewcnt=viewcnt + 1
where pid=#{pid}

update posts
set commcnt=commcnt + #{cnt}
where pid=#{pid}

mapper.select:
    	select p.*, date_format(p.regdate, '%Y-%m-%d %T') as fmtdate, u.uname
		from posts as p, users as u
		where uid=writer and pid=#{pid}

mapper.update:
update posts 
set title=#{title},body=#{body},regdate=now()
where pid=#{pid}
